mapper.update:
update tasklist
set is_enable = #{enable}
where id = #{id}

mapper.select:
SELECT *
FROM tasklist as t1
WHERE t1.id >= (RAND() * (SELECT MAX(id) FROM tasklist)) LIMIT #{size};

SELECT *
FROM tasklist as t1
WHERE id not in (
    select task_id
    from taskfilter
    where user_email = #{email}
)
  and t1.id >= (RAND() * (SELECT MAX(id) FROM tasklist)) limit #{size};
